const DEFAULT_LOG_LEVEL = 'warn';

export class Settings {
  /**
   * Note that the values of these configuration properties must match those defined in the package.json
   */
  private static LOGGING_LEVEL_CONFIG_PROP = 'sap.projectLifecycle.logLevel';

  /**
   * @returns {LogLevel}
   */
  public static getLoggingLevelSetting(): LogLevel {
    const config = vscode.workspace.getConfiguration();
    return config.get(Settings.LOGGING_LEVEL_CONFIG_PROP) === undefined ? DEFAULT_LOG_LEVEL : config.get(Settings.LOGGING_LEVEL_CONFIG_PROP) as LogLevel;
  }
}

import { ActionType } from "simplified-git-common";

interface ActionProps {
    accept: ActionType;
    emit?: ActionType;
    internal?: boolean;
}

export const ActionMapping = ( { accept, emit, internal = false }: ActionProps )  => {
    return (constructor: Function) => {
        constructor.prototype.accept          = accept;
        constructor.prototype.emit            = emit; 
        constructor.prototype.internal        = internal;
    };
}

