'use strict';

import { ActionType, Repository } from 'simplified-git-common';
import * as vscode from 'vscode';
import { Uri } from 'vscode';
import * as path from 'path';
import { CACHED_DATA_KEY } from './common';
import * as Logger from './logger/Logger';

import Sidebar from './views/Sidebar';
import BufferedFileSystemWatcher from './watchers/BufferedFileSystemWatcher';

const initializeLogger = (context: vscode.ExtensionContext) => {
	try {
		Logger.createExtensionLogger(context);
	} catch (error: any) {
		Logger.getLogger().error('Extension activation failed due to logger configuration failure:', error.message);
		return;
	}
	Logger.getLogger().info('simplified-git extension activated');
};

export function activate(context: vscode.ExtensionContext) {
	initializeLogger(context);

	const sidebar = new Sidebar(context);
	context.subscriptions.push(
		vscode.window.registerWebviewViewProvider(Sidebar.viewType, sidebar, {
			webviewOptions: {
				retainContextWhenHidden: true
			}
		})
	);

	context.subscriptions.push(	
		vscode.workspace.onDidChangeWorkspaceFolders( async e => {
			Logger.getLogger().info('Workspace folder(s) changed: %s', e);
			await sidebar.executeAction({action: ActionType.INIT});
		})
	);

	context.subscriptions.push(new BufferedFileSystemWatcher(sidebar));


	context.subscriptions.push(
		vscode.workspace.onDidChangeConfiguration(async e => {
			Logger.getLogger().debug('Workspace configuration changed, we don\'t have any subscribers yet!');
		})
	);

	context.subscriptions.push(
		vscode.commands.registerCommand('simplified-git.collapse', () => {
			vscode.commands.executeCommand('workbench.action.closeSidebar');
		})
	);

	context.subscriptions.push(
		vscode.commands.registerCommand('simplified-git.init-repository', async () => {
			await sidebar.executeAction({action: ActionType.INIT_REPOSITORY});
		})
	);
	context.subscriptions.push(
		vscode.commands.registerCommand('simplified-git.clone-repository', async () => {
			await sidebar.executeAction({action: ActionType.CLONE_REPOSITORY});
		})
	);
}

export function deactivate() {
	Logger.getLogger().info('simplified-git extension de-activated');
}

import { ActionType, Branch, Message } from "simplified-git-common";
import * as vscode from 'vscode';
import { QuickPickItem  } from "vscode";
import { ActionMapping } from "../../decorators";
import { getClassLogger } from "../../logger/Logger";
import GitService from "../../services/GitService";
import ActionHandler from "../base";
import {InputBoxValidationSeverity, InputBoxValidationMessage } from 'vscode';
import GitServiceFactory from "../../services/GitServiceFactory";

interface BranchQuickPickItem extends QuickPickItem {
    name: string;
}

const UNCOMMITTED_CHANGES_MESSAGE = 'You have uncommitted local changes';
const UNCOMMITTED_CHANGES_CHANGES_DETAIL = 'You can either discard the local changes or bring them to your new branch!';
const DISCARD_CHANGES_OPTION = 'Discard Changes';
const BRING_TO_NEW_BRANCH_OPTION = 'Bring changes to new branch'

@ActionMapping({
    accept: ActionType.ADD_NEW_BRANCH,
    emit: ActionType.DONE_SWITCHING_BRANCH
})
export default class AddNewBranchActionHandler implements ActionHandler {

    private readonly logger = getClassLogger('AddNewBranchActionHandler');

    async execute({ body: { repository } }: Message) {
        this.logger.debug('Adding new branch ...');
        const git: GitService = GitServiceFactory.getServiceFor(repository);
        const branches = await git.getBranches();
        const name = await vscode.window.showInputBox({ 
            placeHolder: 'Branch name',
            prompt: 'Please provide a new branch name',
            validateInput: (_name) => {
                const existingBranch = branches.find( branch => branch.name === _name);
                if(existingBranch) {
                    return {message: `Branch '${_name}' already existed`, severity: InputBoxValidationSeverity.Error} as InputBoxValidationMessage;
                }
            }
        });

        if(name) {
            const items = branches.map( ({ name, commit, remote }: Branch)  => {
                let label: string;
                let description: string;

                if(remote) {
                    label = `$(cloud) ${name}`;
                    description = `Remote branch at ${commit}`;
                } else {
                    label = `$(source-control) ${name}`;
                    description = commit;
                }
                return { name, label, description } as BranchQuickPickItem;
            });

            const selected = await vscode.window.showQuickPick(items, { 
                    canPickMany: false, 
                    placeHolder: `Please select a base branch to create the '${name}' branch from` });
            try {
                if(selected) {
                    const changedFiles = await git.getChangedFiles();
                    if(changedFiles.length > 0) {
                        const selectedOption = await vscode.window.showWarningMessage(
                            UNCOMMITTED_CHANGES_MESSAGE,
                            { modal: true, detail: UNCOMMITTED_CHANGES_CHANGES_DETAIL },
                            DISCARD_CHANGES_OPTION,
                            BRING_TO_NEW_BRANCH_OPTION,
                        );

                        if(selectedOption === DISCARD_CHANGES_OPTION) {
                            // @todo: should be simplified
                            await git.discardChanges(changedFiles.map(f => f.path));
                        }
                    }
                    await git.newBranch(name, selected.name);
                }
                return name;
            } catch(e: any) {
                vscode.window.showErrorMessage(e.message);
            }
        }
    };
}

import { ActionType, Message, Repository } from "simplified-git-common";
import * as vscode from 'vscode';
