@ActionMapping({
    accept: ActionType.ADD_REMOTE_REPOSITORY
})
export default class AddRemoteRepositoryActionHandler implements ActionHandler {

    private readonly logger = getClassLogger('AddRemoteRepositoryActionHandler');

    async execute({ body } : Message) {

        if(!body) {
            this.logger.error('Missing repository');
            return;
        }

        const  remoteUrl = await vscode.window.showInputBox({
            placeHolder: 'Remote Repository URL',
            ignoreFocusOut: true
        });

        if(!remoteUrl) {
            this.logger.error('User did not provide a valid remote repository URL');
            return;
        }

        const repository = {...body, url: remoteUrl} as Repository
        const git = GitServiceFactory.getServiceFor(repository);
        await git.addRemoteRepository();
        await git.publishRepository();
        await git.pullRemoteChanges();
    };
}

import * as vscode from 'vscode';
import { ActionType, Message } from "simplified-git-common";
import { ExtensionContext } from "vscode";
import ActionHandler from "../base";
import { getClassLogger } from "../../logger/Logger";
import * as fs from 'fs/promises';
import * as path from 'path';
import { ActionMapping } from "../../decorators";
import simpleGit, { SimpleGit, SimpleGitProgressEvent } from "simple-git";
import { VSCODE_COMMAND_OPEN_FOLDER } from '../../common';
import Sidebar from '../../views/Sidebar';

// const GIT_URL_PATTERN = /(?:git|ssh|https?|git@[-\w.]+):(\/\/)?(.*?)(\.git)(\/?|\#[-\d\w._]+?)$/;
const GIT_URL_PATTERN = /(https?:\/\/|git@)(.+)(:|\/)(?<owner>.+)\/(?<repo>[a-zA-Z0-9_-]+)(.git)?\/?/;

const POST_CLONE_POPUP_MESSAGE = 'Would you like to open the cloned repository, or add it to the current workspace?';

enum PostCloneOptions {
    ADD_TO_WORKSPACE = 'Add to Workspace',
    OPEN_IN_NEW_WINDOW = 'Open in New Window',
    CANCEL = 'Cancel',
    OPEN = 'Open',
}


@ActionMapping({
    accept: ActionType.CLONE_REPOSITORY
})
export default class CloneRepositoryActionHandler implements ActionHandler {

    private readonly logger = getClassLogger('CloneRepositoryActionHandler');

    async execute(_: Message) {
        const remoteGitUrl = await vscode.window.showInputBox({
            placeHolder: 'Provide repository URL',
            ignoreFocusOut: true,
            validateInput: (value: string) => {
                return !GIT_URL_PATTERN.test(value) ? 'Not a valid repository URL' : undefined;
            },
        }) || '';

        if(!remoteGitUrl) {
            this.logger.info('No remote repository URL was provided!');
            return;
        }

        const gitUrlMatcher = remoteGitUrl.trim().match(GIT_URL_PATTERN);
        if(!gitUrlMatcher) {
            throw new Error(`Remote repository URL ${remoteGitUrl} is invalid!`);
        }

        const simpleRepoName = gitUrlMatcher.groups['repo'];
        const repositoryLocation = await vscode.window.showOpenDialog({
            canSelectFiles: false,
            canSelectFolders: true,
            openLabel: 'Select Repository Location',
            canSelectMany: false,
        })

        if(repositoryLocation && repositoryLocation.length > 0) {
            const localPath = repositoryLocation[0].fsPath;
            let repositoryPath = localPath;

            const dirs = await fs.readdir(localPath);
            let repositoryBaseDir = simpleRepoName;
            if(dirs.length > 0) {
                for(let i = 1; ; i++) {
                    if(!dirs.includes(repositoryBaseDir)) {
                        break;
                    }
                    repositoryBaseDir = `${simpleRepoName}-${i}`;
                }
                repositoryPath = path.join(localPath, repositoryBaseDir);
            }

             await vscode.window.withProgress({
                location: vscode.ProgressLocation.Notification,
              }, 
              async ( progress ) => {
                progress.report({
                    message: `Cloning ${remoteGitUrl} to ${repositoryPath}...`
                });

                const git: SimpleGit = simpleGit({ progress: (event: SimpleGitProgressEvent) => {
                        progress.report({
                            message: `Git - ${event.method} ${event.stage} stage ${event.progress}% complete`,
                            increment: event.progress,
                        });
                    }
                });
                await git.clone(remoteGitUrl, repositoryPath);
            });

            const postCloneOption = await vscode.window.showInformationMessage(
                POST_CLONE_POPUP_MESSAGE,
                PostCloneOptions.OPEN,
                PostCloneOptions.CANCEL,
                PostCloneOptions.OPEN_IN_NEW_WINDOW,
                PostCloneOptions.ADD_TO_WORKSPACE,
            );

            const repositoryUri = vscode.Uri.file(repositoryPath);

            if(postCloneOption === PostCloneOptions.OPEN) {
                vscode.commands.executeCommand(VSCODE_COMMAND_OPEN_FOLDER, repositoryUri);
            } else if(postCloneOption === PostCloneOptions.OPEN_IN_NEW_WINDOW) {
                vscode.commands.executeCommand(VSCODE_COMMAND_OPEN_FOLDER, repositoryUri, {forceNewWindow: true});
            } else if(postCloneOption === PostCloneOptions.ADD_TO_WORKSPACE) {
                vscode.workspace.updateWorkspaceFolders(0, 0, { uri: repositoryUri });
            }
        }
    }

    async hasContent(repositoryLocation: string) {
        return (await fs.readdir(repositoryLocation)).length > 0;
    } 
}

