@ActionMapping({
    accept: ActionType.COMMIT_AND_SYNC,
    emit: ActionType.DONE_COMMIT_AND_SYNC,
})
export default class CommitAndSyncActionHandler implements ActionHandler {
    private readonly logger = getClassLogger('CommitAndSyncActionHandler');

    constructor() {}

    async execute({ body: { repository, message} }: Message) {
        this.logger.debug('Committing and syncing repository...');
        const git = GitServiceFactory.getServiceFor(repository)
        try {
            await git.commitAndSync(message);
            vscode.window.showInformationMessage('The repository is synced.');
        } catch(e: any) {
            vscode.window.showErrorMessage(e.message);
        }
    }
