const DISCARD_CHANGES_MESSAGE = 'Are you sure you want to discard all pending changes?';
const DISCARD_CHANGES_DETAIL = 'You will not be able to restore them later.';
const DISCARD_CHANGES_YES_OPTION = 'Yes, Discard All Changes';
const DISCARD_CHANGES_NO_OPTION = 'No, Don\t Discard';

@ActionMapping({
    accept: ActionType.DISCARD_CHANGES
})
export default class DiscardChangesActionHandler implements ActionHandler {

    private readonly logger = getClassLogger('DiscardChangesActionHandler');


    async execute({ body: { repository, files } }: Message) {
        this.logger.debug('Discarding changes...');

        const selectedOption = await vscode.window.showWarningMessage(
                DISCARD_CHANGES_MESSAGE,
                {modal: true, detail: DISCARD_CHANGES_DETAIL},
                { title: DISCARD_CHANGES_NO_OPTION },
                { title: DISCARD_CHANGES_YES_OPTION, isCloseAffordance: true },
        );

        const git = GitServiceFactory.getServiceFor(repository)
        if(selectedOption.title === DISCARD_CHANGES_YES_OPTION) {
            await git.discardChanges(files);
        }
    };
}

import { ActionType, Message, Repository } from "simplified-git-common";
