@ActionMapping({
    accept: ActionType.GET_PENDING_CHANGES,
    emit: ActionType.DONE_GETTING_PENDING_CHANGES
})
export default class GetPendingChangesActionHandler implements ActionHandler {

    private readonly logger = getClassLogger('GetPendingChangesActionHandler');

    async execute( { body: { repository } }: Message, sidebar: Sidebar) {
        this.logger.debug('Getting Pending Changes...');
        const repositories = sidebar.getRepositories();
        const index = repositories.findIndex(_repository => _repository.path === repository.path);
        if(index === -1) {
            throw new Error(`Unable to find repository ${repository.path}` );
        }

        const git = GitServiceFactory.getServiceFor(repository);
        const changedFiles = await git.getChangedFiles();
        repositories[index].changedFiles = changedFiles;
        return changedFiles;
    }
}

