import ActionHandler from "../base";

@ActionMapping({
    accept: ActionType.IGNORE_FILE
})
export default class IgnoreFileActionHandler implements ActionHandler {

    private readonly logger = getClassLogger('IgnoreFileActionHandler');

    constructor() {}

    async execute({ body: { repository, file } }: Message) {
        this.logger.debug('Ignoring changes...');
        const ignorePath = path.join(repository.path, '.gitignore');
        const ignoreFilePresent = await this.isIgnoreFilePresent(ignorePath);
        let ignoreLines: string[] = [];
        if(ignoreFilePresent) {
            const ignoreContent = await fs.readFile(ignorePath, {encoding: 'utf8'});
            if(!isEmpty(ignoreContent)) {
                ignoreLines = ignoreContent.split(/\r?\n/);
            }
        }

        if(!ignoreLines.includes(file)) {
            ignoreLines.push(file);
        }

        await fs.writeFile(ignorePath, ignoreLines.join('\n'));
        vscode.commands.executeCommand('vscode.open', Uri.file(ignorePath));
    };

    private async isIgnoreFilePresent(ignorePath: string) {
        try {
            const stats = await fs.stat(ignorePath);
            return stats.isFile();
        } catch(e: any) {
            return false;
        }
    }
}

import * as vscode from 'vscode';
import * as fs from 'fs/promises';
import * as path from 'path';
import { WorkspaceFolder } from 'vscode';
import ActionHandler from "../base";
import { ActionType, Message, Repository, Folder } from 'simplified-git-common';
import { isQueueEmpty } from '../../utils';
import { getClassLogger } from '../../logger/Logger';
import { ActionMapping } from '../../decorators';
import GitServiceFactory from '../../services/GitServiceFactory';
import Sidebar from '../../views/Sidebar';

const EXLCUDED_DIRS = ['node_modules', '.vscode']

@ActionMapping({
    accept: ActionType.INIT,
    emit: ActionType.DATA_LOADED,
})
export default class InitActionHandler implements ActionHandler {

    private readonly logger = getClassLogger('InitActionHandler');

    private getActiveWorkspaceFolders() : readonly WorkspaceFolder[] {
        return vscode.workspace.workspaceFolders ? vscode.workspace.workspaceFolders : [];
    }

    private async detectGitRepositories(root: WorkspaceFolder) : Promise<Repository[]> {
        const Q: Folder[] = [ { name: root.name, path: root.uri.fsPath } ];
        const repositories: Repository[] = [];
        while(!isQueueEmpty(Q)) {
            const folder = Q.pop();
            if(folder) {
                const files = await fs.readdir(folder.path, {withFileTypes: true});
                const folders = files.filter(entry => entry.isDirectory() && !EXLCUDED_DIRS.includes(entry.name))
                                .map( entry => {
                                    return {name: entry.name, path: path.join(folder.path, entry.name)} as Folder;
                                });

                const hasGit = !!folders.find(folder => folder.name === '.git');   
                if(!hasGit) {
                    Q.push(...folders);
                    continue;
                } 
                repositories.push(await this.getRepository(folder));
            }
        }
        return repositories;
    }

    private async getRepository(folder: Folder) {
        const git = GitServiceFactory.getServiceFor(folder.path);
        return  {
            ...folder,
            active: false,
            url: await git.getRemoteUrl(),
            branches: await git.getLocalBranches(),
            changedFiles: await git.getChangedFiles(),
        } as Repository;
    }

    private async isRepositoryValid(repository: Repository) : Promise<boolean> {
        const gitDirectory = path.join(repository.path, '.git');

        let repositoryValid: boolean;
        try {
            await fs.access(gitDirectory);
            repositoryValid = true;
        } catch(e: any) {
            this.logger.warn('%s is not accessible, the repository might be broken.', gitDirectory);
            repositoryValid = false;
        }
        return repositoryValid;
    }

    async execute (_: Message, sidebar: Sidebar) {
        this.logger.debug('Processing Init Action...');
        const workspaceFolders = this.getActiveWorkspaceFolders();
        if(workspaceFolders.length === 0) {
            sidebar.clearRepositories();
            return undefined;
        }
        const repositories: Repository[] = [];
        for(const cachedRepository of sidebar.getRepositories()) {
            if(!!workspaceFolders.find(it => cachedRepository.path.includes(it.uri.fsPath)) && 
            await this.isRepositoryValid(cachedRepository) ) {
                repositories.push(cachedRepository);
            }
        }

        const addedRepositories = new Map<string, boolean>();
        repositories.forEach(repository => addedRepositories.set(repository.path, true));

        for(const workspaceFolder of workspaceFolders) {
            const detectedRepositories = await this.detectGitRepositories(workspaceFolder);
            detectedRepositories.forEach( repository => {
                 const added = addedRepositories.get(repository.path);
                 this.logger.debug('Repository: %s - %s',  repository.path, added ? 'added' : 'new');
                 if(!added) {
                    repositories.push(repository);
                    addedRepositories.set(repository.path, true);
                 }
            });
        }

        const activeRepository = repositories.find(({active}: Repository) => active);
        if(!activeRepository && repositories.length > 0) {
            repositories[0].active = true;
        }
        sidebar.reInitRepositories(repositories);
        return repositories;        
    }
}

import { ActionType, Message, Repository } from "simplified-git-common";
import * as vscode from 'vscode';
import * as path from 'path';
import { QuickPickItem } from "vscode";
import ActionHandler from "../base";
import { getClassLogger } from "../../logger/Logger";
import { ActionMapping } from "../../decorators";
import GitServiceFactory from "../../services/GitServiceFactory";
import Sidebar from "../../views/Sidebar";

const WORKSPACE_FOLDER_PICK_PLACEHOLDER = 'Pick workspace folder to initialize repo in';

@ActionMapping({
    accept: ActionType.INIT_REPOSITORY,
    emit: ActionType.DONE_INIT_REPOSITORY,
})
export default class InitRepositoryActionHandler implements ActionHandler {
    private readonly logger = getClassLogger('InitRepositoryActionHandler');

    async execute(_: Message, sidebar: Sidebar) {
        this.logger.debug('Initiating Git repository...');

        // const data = context.workspaceState.get(CACHED_DATA_KEY);
        const workspaceFolders = vscode.workspace.workspaceFolders || [];
        const items: QuickPickItem[] = workspaceFolders.filter( folder => {
            const repository = sidebar.getRepositories().find(repository => 
                folder.uri.fsPath === repository.path ||
                repository.path.includes(path.join(folder.uri.fsPath, path.sep)));

            return !repository;
        }).map( folder => {
            return { label: folder.name, description: folder.uri.path } as QuickPickItem;
        });

        const chosen = await vscode.window.showQuickPick(items, 
                                        { placeHolder: WORKSPACE_FOLDER_PICK_PLACEHOLDER, matchOnDescription: true});
        if(chosen) {
            this.logger.info(`User picked ${chosen.description}`);

            const remoteUrl = await vscode.window.showInputBox({ placeHolder: 'Remote Repository URL', ignoreFocusOut: true });
            if(!remoteUrl) {
                this.logger.error('User did not provide a valid remote repository URL');
                return;
            }
            const repository = { name: chosen.label, path: chosen.description, url: remoteUrl } as Repository;
            const git = GitServiceFactory.getServiceFor(repository);
            await git.init();
            await git.addRemoteRepository();
            await git.publishRepository();
            const newRepository = {
                ...repository,
                url: await git.getRemoteUrl(),
                branches: await git.getLocalBranches(),
                changedFiles: await git.getChangedFiles(),
                updatedAt: Date.now(),
            } as Repository;

            sidebar.addRepository(newRepository);
            vscode.window.showInformationMessage(`Successfully initialized and published repository '${repository.name}'.`);

            return newRepository;
        }
    }
}

