@ActionMapping({
    accept: ActionType.RELOAD_REPOSITORY,
    emit: ActionType.DONE_RELOAD_REPOSITORY,
})
export default class ReloadRepositoryActionHandler implements ActionHandler {

    private readonly logger = getClassLogger('ReloadRepositoryActionHandler');

    private async isRepositoryValid(repository: Repository) : Promise<boolean> {
        const gitDirectory = path.join(repository.path, '.git');

        let repositoryValid: boolean;
        try {
            await fs.access(gitDirectory);
            repositoryValid = true;
        } catch(e: any) {
            this.logger.warn('%s is not accessible, the repository might be broken.', gitDirectory);
            repositoryValid = false;
        }
        return repositoryValid;
    }

    async execute({ body: { repository } }: Message, sidebar: Sidebar) {
        this.logger.debug('Reloading repository...');

        if(!await this.isRepositoryValid(repository)) {
            // Repository might be destroyed or removed
            const brokenRepository = { name: repository.name,  path: repository.path } as Repository;
            sidebar.removeRepository(brokenRepository);
            return brokenRepository;
        }

        const git = GitServiceFactory.getServiceFor(repository);
        const reloadedRepository = {
                ...repository,
                url: await git.getRemoteUrl(),
                branches: await git.getLocalBranches(),
                changedFiles: await git.getChangedFiles(),
                updatedAt: Date.now(),
        } as Repository;
        sidebar.addRepository(reloadedRepository);
        return reloadedRepository;
    }
}

