@ActionMapping({
    accept: ActionType.SET_ACTIVE_REPOSITORY,
    emit: ActionType.DONE_SET_ACTIVE_REPOSITORY,
})
export default class SetActiveRepositoryActionHandler implements ActionHandler {

    private readonly logger = getClassLogger('SetActiveRepositoryActionHandler');

    async execute( message: Message, sidebar: Sidebar) {
        this.logger.debug('Setting active repository ...');
        const repositoryPath = message.body as string;
        const repositories = sidebar.getRepositories();

        const repository = repositories.find(repository => repository.path === repositoryPath);
        if(!repository) {
            throw new Error(`Unable to find repository ${repositoryPath}`);
        }
        for(const repository of repositories) {
            repository.active = repository.path === repositoryPath;
        }

        return repository;
    }
}

