const SWITCH_BRANCH_DIALOG_MESSAGE = 'You have changes on this branch that have not been committed and synced.';
const SWITCH_BRANCH_DIALOG_DETAIL = 'Switching branches will move these changes to the other branch.';
const  SWITCH_AND_MOVE = 'Switch & Move Changes';

@ActionMapping({
    accept: ActionType.SWITCH_BRANCH,
    emit: ActionType.DONE_SWITCHING_BRANCH
})
export default class SwitchBranchActionHandler implements ActionHandler {

    private readonly logger = getClassLogger('SwitchBranchActionHandler');

    async execute({ body: { source, target, repository } }: Message) {
        this.logger.debug(`Switching branch from ${source} to ${target}...`);
        const git = GitServiceFactory.getServiceFor(repository);

        let targetBranch = target;
        const changedFiles = await git.getChangedFiles();
        if(!isEmpty(changedFiles)) {
            const selectedOption = await vscode.window.showWarningMessage(
                SWITCH_BRANCH_DIALOG_MESSAGE, 
                { modal: true, detail: SWITCH_BRANCH_DIALOG_DETAIL },
                SWITCH_AND_MOVE
            );

            if(selectedOption === SWITCH_AND_MOVE) {
                await git.switchBranch(targetBranch);
                this.logger.info('Done switching to %s', targetBranch);
            } else {
                this.logger.info('User canceled the operation, nothing changed!');
                targetBranch = source;
            }
        } else {
            await git.switchBranch(targetBranch);
        }
        return targetBranch;       
    };
}

