import { ActionType, Message, Repository } from "simplified-git-common";
import ActionHandler from "./base";
import * as handlers from './all';
import { getClassLogger } from "../logger/Logger";
import DefaultActionHandler from "./default";
import * as vscode from 'vscode';
import Sidebar from "../views/Sidebar";


export class ActionProxy implements ActionHandler {

    private readonly logger = getClassLogger('ActionProxy');
    private readonly actions = new Map<ActionType, ActionHandler>();

    constructor(
        private readonly sidebar: Sidebar
    ) {
        this.autoRegisterActionHandlers();
    }

    private autoRegisterActionHandlers() {
        for (const HandlerClass of Object.values(handlers)) {
            this.logger.info('Registering action handler %s ...', HandlerClass.name);
            const handler = new HandlerClass() as ActionHandler;

            if(handler.accept) {
                this.actions.set(handler.accept, handler);
            }

            this.logger.info('Registered %s successfully', HandlerClass.name);
        }
    }


    public async execute (message: Message) :  Promise<any> {
        const handler: ActionHandler = this.actions.get(message.action) || new DefaultActionHandler;
        try {
            const body = await handler.execute(message, this.sidebar);
            if(handler.emit) {
                await this.sidebar.postMessage({action: handler.emit, body });
                if(handler.emit === ActionType.DONE_RELOAD_REPOSITORY || 
                        handler.emit === ActionType.DONE_SET_ACTIVE_REPOSITORY) {

                    if(body) {
                        const repositories = this.sidebar.getRepositories();
                        const index = repositories.findIndex(repository => repository.path === body.path);
                        if(index !== -1) {
                            repositories[index] = body;
                            const repository = body as Repository;
                            if(repository.active) {
                                this.sidebar.updateBadge(repository);
                            }
                        }
                    }
                } else if(handler.emit === ActionType.DATA_LOADED) {
                    if(body) {
                        const repositories = body as Repository[];
                        const repository = repositories.find(_ => _.active);
                        this.sidebar.updateBadge(repository);
                    }
                }
            }            
        } catch(e: any) {
            this.logger.error('Handler[%s] - Unexpected error: %s', message.action, e.message);
            vscode.window.showErrorMessage(e.message);
        }
    }

}

import * as vscode from 'vscode';
import * as _ from 'lodash';
import { getExtensionLogger, getExtensionLoggerOpts, IChildLogger, IVSCodeExtLogger, LogLevel } from '@vscode-logging/logger';
import { Settings } from '../common/Settings';

/**
 * A Simple Wrapper to hold the state of our 'singleton' (per extension) IVSCodeExtLogger
 * implementation.
 */

/**
 * @type {IVSCodeExtLogger}
 */
let logger: any;

/**
 * Note the use of a getter function so the value would be lazy resolved on each use.
 * This enables concise and simple consumption of the Logger throughout our Extension.
 *
 * @returns { IVSCodeExtLogger }
 */
export function getLogger(): IVSCodeExtLogger {
  if (_.isNil(logger)) {
    throw Error('ERROR_LOGGER_NOT_INITIALIZED');
  }
  return logger;
}

export function getClassLogger(className: string): IChildLogger {
	return getLogger().getChildLogger({label:className});
}

/**
 * This function should be invoked after the Logger has been initialized in the Extension's `activate` function.
 * @param {IVSCodeExtLogger} newLogger
 */
function initLoggerWrapper(newLogger: any) {
  logger = newLogger;
}

export function createExtensionLogger(context: vscode.ExtensionContext) {
	const contextLogPath = context.logUri.fsPath;
	const logLevelSetting: LogLevel = Settings.getLoggingLevelSetting();
	const logOutputChannel = vscode.window.createOutputChannel('simplified-git');
	const extensionLoggerOpts: getExtensionLoggerOpts = {
		extName: 'simplified-git',
		level: logLevelSetting,
		logPath: contextLogPath,
		logOutputChannel: logOutputChannel,
		logConsole: true
	};

	// The Logger must first be initialized before any logging commands may be invoked.
	const extensionLogger = getExtensionLogger(extensionLoggerOpts);
	// Update the logger-wrapper with a reference to the extLogger.
	initLoggerWrapper(extensionLogger);
	logLoggerDetails(context, logLevelSetting);
}

function logLoggerDetails(context: vscode.ExtensionContext, configLogLevel: string): void {
    getLogger().info('Logger initialized for simplified-git');
    getLogger().info(`Start logging with log level: ${configLogLevel}`);
    getLogger().info(`Full logs can be found in the folder: ${context.logUri.fsPath}`);  
}

