    ExtensionContext,
    Uri,
} from 'vscode';
import { Message, Repository } from 'simplified-git-common';
import { ActionProxy } from '../handlers/proxy';

export default class Sidebar implements WebviewViewProvider {

    public static readonly viewType = 'simplified-git.sidebar';
    private _view?: WebviewView;
    private readonly outDir: Uri;
    // Cached repositories
    private readonly repositories: Repository[] = [];

    private actionProxy: ActionProxy;
    constructor( 
        private readonly extensionContext: ExtensionContext
    ) {
        this.outDir = Uri.joinPath(this.extensionContext.extensionUri, 'out');
    }

    public getExtensionContext() : ExtensionContext {
        return this.extensionContext;
    } 

    async executeAction(message: Message) {
        await this.actionProxy.execute(message);
    }

    async resolveWebviewView(webviewView: WebviewView, 
                       _context: WebviewViewResolveContext<unknown>, 
                       _token: CancellationToken): Promise<void> {

        this._view = webviewView;

        this._view.webview.options = {
            enableScripts: true,
            localResourceRoots: [
                this.extensionContext.extensionUri
            ]
        };        

        this.actionProxy = new ActionProxy(this);
        this._view.webview.html = await this._getHtmlForWebview(webviewView.webview);     
        this._view.webview.onDidReceiveMessage( async message => await this.executeAction(message) );
        this._view.onDidDispose(() => this.clearRepositories());
    }

    private async  _getHtmlForWebview(webview: Webview) {
        const templateUri = Uri.joinPath(this.outDir, 'views', 'sidebar.template.html');
        const bundleScriptUri = webview.asWebviewUri(vscode.Uri.joinPath(this.outDir, 'app', 'bundle.js'));

        const html = await fs.readFile(templateUri.fsPath, {encoding: 'utf-8'});
        return html.replace('{{bundleScriptPath}}', bundleScriptUri.toString());
    }

    public getRepositories() : Repository[] {
        return this.repositories;
    }

    public removeRepository(repository: Repository) {
        const index = this.repositories.findIndex(it => it.path === repository.path);
        this.repositories.splice(index, 1);
    }

    public reInitRepositories(repositories: Repository[]) {
        this.clearRepositories();
        this.repositories.push(...repositories);
    }

    public clearRepositories() {
        this.repositories.splice(0, this.repositories.length);
    }

    public async postMessage(message: Message) {
        await this._view.webview.postMessage(message);
    }

    public addRepository(newRepository: Repository) {
        if(newRepository) {
            const repository = this.repositories.find(it => it.path === newRepository.path);
            if(!repository) {
                this.repositories.push(newRepository);
            }
        }
    }

    public updateBadge(repository: Repository) {
        const numOfPendingChanges = repository.changedFiles ? repository.changedFiles.length : 0;
        this._view.badge = {tooltip: `${numOfPendingChanges} pending changes`, value: numOfPendingChanges};
    }

}

import { ActionType, Repository } from 'simplified-git-common';
import * as vscode from 'vscode';
import * as path from 'path';
import * as fs from 'fs/promises';
import { Disposable, Uri } from "vscode"
import { getClassLogger } from '../logger/Logger';
import { isEmpty } from 'lodash';
import Sidebar from '../views/Sidebar';
import { setTimeout } from 'timers/promises';


enum FileOperation {
    CHANGE,
    CREATE,
    DELETE,
}

export default class BufferedFileSystemWatcher implements Disposable {
    private logger = getClassLogger('BufferedWorkspaceEventHandler');
    private readonly waitlist: Repository[] = [];
    private readonly disposables: Disposable[]  = [];

    constructor(
        private readonly sidebar: Sidebar,
        private readonly delayInMillis = 2000
    ) {
        this.registerListeners();
    }

    private registerListeners() {
        const watcher = vscode.workspace.createFileSystemWatcher('**/*');
        this.disposables.push(watcher.onDidChange(async uri => {this.handleFileChange(uri, FileOperation.CHANGE)}));
        this.disposables.push(watcher.onDidCreate(async uri => {this.handleFileChange(uri, FileOperation.CREATE)}));
        this.disposables.push(watcher.onDidDelete(async uri => {this.handleFileChange(uri, FileOperation.DELETE)}));
    }

    private isInWaitlist(repository: Repository) : boolean {
        return this.waitlist.findIndex(it => it.path === repository.path) !== -1;
    }

    private async handleFileChange(uri: Uri, operation: FileOperation) {

		let repository = this.sidebar.getRepositories().find(repository => 
					uri.fsPath === repository.path ||
					uri.fsPath.includes(path.join(repository.path, path.sep)));

        if(!repository) {
            if(uri.fsPath.endsWith('/.git') && operation === FileOperation.CREATE) {
                const gitRootDirectory = uri.fsPath.split('/.git')[0];
                repository = { name: path.basename(gitRootDirectory), path: gitRootDirectory } as Repository;
            } else {
                return;
            }
        }

        // Don't add it to waitlist, if the repository is already in it        
        if(this.isInWaitlist(repository)) {
            this.logger.warn(`Repository '${repository.path}' already in the waitlist.`);
            return;
        }

        this.waitlist.push(repository);
        await this.processItemsInWaitlist();
    }

    private async processItemsInWaitlist() {

        while(!isEmpty(this.waitlist)) {
            const repository = this.waitlist[0];
            this.logger.debug('Reloading repository %s...', repository.path);
            await this.sidebar.executeAction({action: ActionType.RELOAD_REPOSITORY, body: { repository }});
            await setTimeout(this.delayInMillis);
            this.waitlist.shift();
            this.logger.debug('Done and removed it from the waitlist!');
        }
    }

    dispose() {
        while(!isEmpty(this.disposables)) {
            const x = this.disposables.pop();
            x.dispose();
        }
    }

}
