import { ActionType } from "simplified-git-common";

interface ActionProps {
    accept: ActionType;
    emit?: ActionType;
    internal?: boolean;
}

export const ActionMapping = ( { accept, emit, internal = false }: ActionProps )  => {
    return (constructor: Function) => {
        constructor.prototype.accept          = accept;
        constructor.prototype.emit            = emit; 
        constructor.prototype.internal        = internal;
    };
}

import { ActionType, Message, Repository } from "simplified-git-common";
import ActionHandler from "./base";
import * as handlers from './all';
import { getClassLogger } from "../logger/Logger";
import DefaultActionHandler from "./default";
import * as vscode from 'vscode';
import Sidebar from "../views/Sidebar";


export class ActionProxy implements ActionHandler {

    private readonly logger = getClassLogger('ActionProxy');
    private readonly actions = new Map<ActionType, ActionHandler>();

    constructor(
        private readonly sidebar: Sidebar
    ) {
        this.autoRegisterActionHandlers();
    }

    private autoRegisterActionHandlers() {
        for (const HandlerClass of Object.values(handlers)) {
            this.logger.info('Registering action handler %s ...', HandlerClass.name);
            const handler = new HandlerClass() as ActionHandler;

            if(handler.accept) {
                this.actions.set(handler.accept, handler);
            }

            this.logger.info('Registered %s successfully', HandlerClass.name);
        }
    }


    public async execute (message: Message) :  Promise<any> {
        const handler: ActionHandler = this.actions.get(message.action) || new DefaultActionHandler;
        try {
            const body = await handler.execute(message, this.sidebar);
            if(handler.emit) {
                await this.sidebar.postMessage({action: handler.emit, body });
                if(handler.emit === ActionType.DONE_RELOAD_REPOSITORY || 
                        handler.emit === ActionType.DONE_SET_ACTIVE_REPOSITORY) {

                    if(body) {
                        const repositories = this.sidebar.getRepositories();
                        const index = repositories.findIndex(repository => repository.path === body.path);
                        if(index !== -1) {
                            repositories[index] = body;
                            const repository = body as Repository;
                            if(repository.active) {
                                this.sidebar.updateBadge(repository);
                            }
                        }
                    }
                } else if(handler.emit === ActionType.DATA_LOADED) {
                    if(body) {
                        const repositories = body as Repository[];
                        const repository = repositories.find(_ => _.active);
                        this.sidebar.updateBadge(repository);
                    }
                }
            }            
        } catch(e: any) {
            this.logger.error('Handler[%s] - Unexpected error: %s', message.action, e.message);
            vscode.window.showErrorMessage(e.message);
        }
    }

}

import { ActionType, Message } from "simplified-git-common";
import { ActionMapping } from "../decorators";
import ActionHandler from "./base";

@ActionMapping({
    accept: ActionType.UNKNOWN
})
export default class DefaultActionHandler implements ActionHandler {

    __name__ = 'DefaultActionHandler';

    async execute(message: Message) {
        console.warn('No handler was found for Action: ', message.action, ' using default one');
    }
}

import { isString } from "lodash";
import { Repository } from "simplified-git-common";
import GitService from "./GitService";
import GitServiceImpl from "./GitServiceImpl";

const UNINITIATED_REPOSITORY = {
    active: false,
    name: '',
    path: '',
    url: '',
    branches: [],
} as Repository;

export default class GitServiceFactory {
    static  getServiceFor(repositoryLike: Repository | string) : GitService {
        const repository = isString(repositoryLike) ? 
                                {...UNINITIATED_REPOSITORY, path: repositoryLike} : repositoryLike;
        return new GitServiceImpl(repository);
    }
}

import { CleanOptions, simpleGit, SimpleGit } from 'simple-git';
import { getClassLogger } from '../logger/Logger';
import GitService from "./GitService";
import { Branch, ChangedFile, FileStatus, Repository } from 'simplified-git-common';
import { basename } from 'path';
import { DEFAULT_BRANCH, DEFAULT_REMOTE } from '../common';
import { isEmpty } from 'lodash';

const DEFAULT_BRANCH_OBJECT = { 
    name: DEFAULT_BRANCH,
    detached: false,
    remote: false,
    current: true,
    commit: '',
    label: ''
} as Branch;

export default class GitServiceImpl implements GitService {

    private readonly git: SimpleGit;
    private readonly logger = getClassLogger('GitServiceImpl');

    constructor(private readonly repository: Repository ) {
        this.git = simpleGit(repository.path);
    }

    public async getRemoteUrl(remote = DEFAULT_REMOTE) {        
        let remoteUrl: string | void = undefined;
        try {
            remoteUrl = await this.git.remote(['get-url', '--all', remote]);
        } catch (e: any) {
            this.logger.warn(`Failed to get remote: ${e}`);
        }
        return remoteUrl;
    }

    public async publishBranch(branch: Branch, remote = DEFAULT_REMOTE) {

        if(!branch.commit) {
            await this.publishRepository(branch.name);
            return;
        }

        // Perform a fetch action to prune branches and tags
        await this.git.fetch(['--prune', '--prune-tags']);
        const trackedBranch = await this.getTrackedBranch(branch.name);
        if(!trackedBranch) {
            await this.git.push([remote, '--set-upstream', branch.name]);
        } else {
            await this.git.push(remote, branch.name);
        }
    }

    public async getLocalBranches() : Promise<Branch[]> {
        const branchSummary = await this.git.branchLocal();

        const branches: Branch[] = [];
        for(const name of branchSummary.all) {
            const branchInfo = branchSummary.branches[name];        
            const detached = branchSummary.detached;
            const branch = { ...branchInfo, remote: false, detached } as Branch;
            if(!detached) {
                branch.target = await this.getTrackedBranch(name);
            }
            branches.push(branch);
        }

        isEmpty(branches) && branches.push(DEFAULT_BRANCH_OBJECT);
        return branches;
    }

    public async switchBranch(name: string) : Promise<void> {
        await this.git.checkout(name, ['--merge']);
    }

    public async init() : Promise<void> {
        await this.git.init(false, ['-b', DEFAULT_BRANCH]);
    }

    public async clone(url: string, to: string) : Promise<void> {
        await this.git.clone(url, to, ['--progress', '--verbose']);
    }

    public async getChangedFiles() : Promise<ChangedFile[]> {
        const statusResult = await this.git.status();
        const changedFiles: ChangedFile[] = [];

        if(!isEmpty(statusResult.conflicted)) {
            statusResult.conflicted.forEach( path => {
                const name = basename(path);
                changedFiles.push({ name, path, status: FileStatus.CONFLICTED });
            });
        }

        if(!isEmpty(statusResult.deleted)) {
            statusResult.deleted.forEach( path => {
                const name = basename(path);
                changedFiles.push({ name, path, status: FileStatus.DELETED });
            });
        }

        if(!isEmpty(statusResult.not_added)) {
            statusResult.not_added.forEach( path => {
                const name = basename(path);
                changedFiles.push({ name, path, status: FileStatus.UNTRACKED });
            });
        }

        if(!isEmpty(statusResult.created)) {
            statusResult.created.forEach( path => {
                const name = basename(path);
                changedFiles.push({ name, path, status: FileStatus.CREATED });
            });
        }

        if(!isEmpty(statusResult.modified)) {
            statusResult.modified.forEach( path => {
                const name = basename(path);
                changedFiles.push({ name, path, status: FileStatus.MODIFIED });
            });
        }

        return changedFiles;
    }

    public async addRemoteRepository() : Promise<void> {
        try {
            await this.git.raw(['ls-remote', '--exit-code', this.repository.url]);
            await this.git.addRemote(DEFAULT_REMOTE, this.repository.url);
            // await this.git.fetch(DEFAULT_REMOTE);
        } catch(e: any) {
            // rollback
            this.logger.error(e.message);
            await this.git.removeRemote(DEFAULT_REMOTE);
            throw new Error(`Failed to add remote ${DEFAULT_REMOTE} ${this.repository.url}`);
        }
    }

    public async publishRepository(branch = DEFAULT_BRANCH): Promise<void> {
        await this.git.add(['--all']);
        const currentDate = new Date().toLocaleString();
        const message = `Initial Commit @${currentDate}`;
        await this.git.commit(message, ['--allow-empty']);
        await this.git.branch(['-M', branch]);
        await this.git.push(DEFAULT_REMOTE, branch, ['--set-upstream', '--force']);
    }

    public async pullRemoteChanges() : Promise<void> {
        const currentBranch = this.repository.branches.find(branch => branch.current);
        if(currentBranch.detached) {
            throw new Error('Pull on detached commit is not supported');
        }

        const trackedBranch = await this.getTrackedBranch(currentBranch.name);
        if(!trackedBranch) {
            throw new Error('No tracking branch');
        }
        await this.git.pull(DEFAULT_REMOTE, currentBranch.name);
    }

    public async commitAndSync(message: string): Promise<void> {

        const currentBranch = this.repository.branches.find(branch => branch.current);
        if(currentBranch.detached) {
            throw new Error('Pull on detached commit is not supported');
        }
        await this.git.fetch(['--no-tags', '--prune', '--all']);
        await this.git.add(['--all']);
        await this.git.commit(message);

        const trackedBranch = await this.getTrackedBranch(currentBranch.name);
        const options: string[] = [];
        let pullRequired = true;
        if(isEmpty(trackedBranch)) {
            const exitCode = await this.git.listRemote(['--exit-code', '--heads', DEFAULT_REMOTE, currentBranch.name]);
            if(exitCode === '0') { // Remote does not exist
                await this.git.branch(['--set-upstream-to', `${DEFAULT_REMOTE}/${currentBranch.name}`, currentBranch.name]);
            } else {
                options.push('--set-upstream');
                pullRequired = false;
            }
        }
        pullRequired && await this.git.pull(DEFAULT_REMOTE, currentBranch.name);
        await this.git.push(DEFAULT_REMOTE, currentBranch.name, options);
    }

    public async discardChanges(files?: string[]) : Promise<void> {
        await this.git.raw(['restore', this.repository.path]);
        await this.git.clean(CleanOptions.FORCE + CleanOptions.RECURSIVE);
    }

    private async getTrackedBranch(branch: string) : Promise<string|undefined> {
        try {
            return await this.git.revparse(['--abbrev-ref', `${branch}@{upstream}`]);
        } catch(e: any) {
            this.logger.warn(`Unexpected error: ${e.message}`);
            return undefined;
        }
    }

    public async newBranch(name: string, target: string) : Promise<void> {
        await this.git.checkout(['-b', name, target]);
    }

    public async getBranches() : Promise<Branch[]> {
        // await this.git.fetch(['--no-tags', '--prune', '--all']);
        const branchSummary = await this.git.branch(['--all', '--verbose']);
        const branches: Branch[] = [];
        let detached = false;
        let remote = false;
        branchSummary.all.forEach( name => {
            const branchInfo = branchSummary.branches[name];
            if(name === branchInfo.name) {
                detached = branchSummary.detached;
            }
            if(name.startsWith('remotes/')) {
                name = name.replace('remotes/', '');
                remote = true;
            }
            branches.push( {...branchInfo, name, remote, detached });
        });

        // if(branches.length === 0) {
        //     branches.push(DEFAULT_BRANCH_OBJECT);
        // }
        return branches;
    }
}

export const DEFAULT_BRANCH = 'main';
export const DEFAULT_REMOTE = 'origin';
export const CACHED_DATA_KEY = 'data';

// VSCode built-in commands
export const VSCODE_COMMAND_OPEN_FOLDER = 'vscode.openFolder';
